import React from 'react';
import { connect } from 'react-redux';
import { createStructuredSelector } from 'reselect';


import CartIcon from '../cart-icon/cart-icon.component';
import CartDropdownContainer from '../cart-dropdown/cart-dropdown.container';
import { selectCartHidden } from '../../redux/cart/cart.selectors';
import { selectCurrentUser } from '../../redux/user/user.selectors';
import { signOutStart } from '../../redux/user/user.actions'

import {ReactComponent as Logo} from  '../../assets/crown.svg'

import { HeaderContainer, LogoContainer, OptionsContainer, OptionLink } from './header.styles';

const Header = ({ currentUser, hidden, signOutStart }) => (
    <HeaderContainer>
        <LogoContainer to="/">
            <Logo className='logo'/>
        </LogoContainer>
        <OptionsContainer>
            <OptionLink to="/shop">
                SHOP
            </OptionLink>
            {/* <OptionLink to="/shop">
                CONTACT
            </OptionLink> */}
            {
                currentUser ? (
                <OptionLink as='div' onClick={signOutStart}>
                    SIGN OUT
                </OptionLink>
               ) : (
                <OptionLink  to='/signin'>
                    SIGN IN
                </OptionLink>
                )}
                <CartIcon/>
        </OptionsContainer>
     {
            hidden 
            ? null 
            : <CartDropdownContainer/>

     }
    </HeaderContainer>

);

//variable can be named anything but mapStateToProps is standard with redux codebases.
const mapStateToProps = createStructuredSelector({
    currentUser: selectCurrentUser,
    hidden: selectCartHidden
});

const mapDispatchToProps = dispatch => ({
    signOutStart: () => dispatch(signOutStart())
})

export default connect(mapStateToProps, mapDispatchToProps)(Header); // connect(mapStateToProps)(header) is used to 'connect' or give access to the state from redux to header
