import React from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import { createStructuredSelector } from 'reselect';


import { auth } from '../../firebase/firebase.utils';
import CartIcon from '../cart-icon/cart-icon.component';
import CartDropdown from '../cart-dropdown/cart-dropdown.component';
import { selectCartHidden } from '../../redux/cart/cart.selectors';
import { selectCurrentUser } from '../../redux/user/user.selectors';

import {ReactComponent as Logo} from  '../../assets/crown.svg'

import './header.styles.scss'
import { HeaderContainer, LogoContainer, OptionsContainer, OptionDiv, OptionLink } from './header.styles';

const Header = ({ currentUser, hidden }) => (
    <div className='header'>
        <LogoContainer to="/">
            <Logo className='logo'/>
        </LogoContainer>
        <OptionsContainer>
            <OptionLink to="/shop">
                SHOP
            </OptionLink>
            <OptionLink to="/shop">
                CONTACT
            </OptionLink>
            {
                currentUser ? (
                <OptionDiv onClick={()=> auth.signOut()}>
                    SIGN OUT
                </OptionDiv>
               ) : (
                <OptionLink className='option' to='/signin'>
                    SIGN IN
                </OptionLink>
                )}
                <CartIcon/>
        </OptionsContainer>
     {
            hidden 
            ? null 
            : <CartDropdown/>

     }
    </div>

);

//variable can be named anything but mapStateToProps is standard with redux codebases.
const mapStateToProps = createStructuredSelector({
    currentUser: selectCurrentUser,
    hidden: selectCartHidden
})

export default connect(mapStateToProps)(Header); // connect(mapStateToProps)(header) is used to 'connect' or give access to the state from redux to header
